
$(document).ready(function(){prikaziNovaBukva();setInterval(prikaziNovaBukva,5000)});function prikaziNovaBukva(){var a=$(".bukvi li:visible").eq();var b=getRandomInt(0,30);while(b===a){b=getRandomInt(0,30)}$(".bukvi li").hide();$(".bukvi li").eq(b).show()}function getRandomInt(b,a){return Math.floor(Math.random()*(a-b))+b};