
$(document).ready(function(){prikaziNovaBukva();setInterval(prikaziNovaBukva,5000)});function prikaziNovaBukva(){var b=$(".bukvi li:visible");var a=getRandomInt(0,30);$(".bukvi li").hide();if(a===b.eq()){prikaziNovaBukva()}else{$(".bukvi li").eq(a).show()}}function getRandomInt(b,a){return Math.floor(Math.random()*(a-b))+b};