
$(document).ready(function(){prikaziNovaBukva();setInterval(prikaziNovaBukva,5000)});function prikaziNovaBukva(){var a=$(".bukvi li:visible").eq();var b=getRandomInt(0,30);if(b!==a){$(".bukvi li").hide();$(".bukvi li").eq(b).show()}else{prikaziNovaBukva()}}function getRandomInt(b,a){return Math.floor(Math.random()*(a-b))+b};